import AddCustomer from "@/views/AddCustomer";
import ListCustomer from "@/views/ListCustomer";
import AddSellJh from "@/views/AddSellJh";
import ListSellJh from "@/views/ListSellJh";
import ListCustOrder from "@/views/ListCustOrder.vue";
import ListAfter from "@/views/ListAfter.vue";
import AddMenus from "@/views/AddMenus.vue";
import RoleManager from "@/views/RoleManager.vue";
import UserManager from "@/views/UserManager.vue";
import Category from "@/views/Category.vue";
import ItemManager from "@/views/ItemManager.vue";
import BuyListManager from "@/views/BuyListManager.vue";
import InStoreList from "@/views/InStoreList.vue";
import OutStoreList from "@/views/OutStoreList.vue";
import ListStore from "@/views/ListStore.vue";
import CustomerArea from "@/views/CustomerArea.vue";
import YearMonthCount from "@/views/YearMonthCount.vue";
import EmployeeCount from "@/views/EmployeeCount.vue";
import AfterSaleCount from "@/views/AfterSaleCount.vue";
import YearNumCount from "@/views/YearNumCount.vue";
import { onMounted, ref } from "vue";
import axios from 'axios';
import { ElMessage, ElLoading } from 'element-plus';

// 声明数组保存所有组件

export default {
  __name: 'index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const views = [AddCustomer, ListCustomer, ListAfter, ListCustOrder, AddSellJh, CustomerArea, YearMonthCount, ListSellJh, AddMenus, UserManager, RoleManager,, Category, ItemManager, BuyListManager, InStoreList, OutStoreList, ListStore, EmployeeCount, AfterSaleCount, YearNumCount];
    // 声明变量保存当前需要显示的组件名
    const currentComponent = ref(views[0]);
    // 声明菜单对象集合数据
    const menus = ref([]);
    // 加载状态
    const loading = ref(false);

    /* 菜单选中处理函数 */
    const handlerSelect = async id => {
      loading.value = true;
      try {
        const {
          data
        } = await axios.get("http://localhost:8081/compIndex?id=" + id);
        currentComponent.value = views[data];
      } catch (error) {
        ElMessage.error('加载组件失败: ' + error.message);
        console.error('加载组件失败:', error);
      } finally {
        loading.value = false;
      }
    };

    // 页面加载时获取菜单数据
    onMounted(async () => {
      const loadingInstance = ElLoading.service({
        lock: true,
        text: '加载菜单中...',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      try {
        const {
          data
        } = await axios.get("http://localhost:8081/listMenus");
        if (!Array.isArray(data)) {
          // 验证数据结构
          throw new Error("菜单数据格式错误");
        }
        menus.value = data;
      } catch (error) {
        ElMessage.error('加载菜单失败: ' + error.message);
      } finally {
        loadingInstance.close();
      }
    });
    const __returned__ = {
      views,
      currentComponent,
      menus,
      loading,
      handlerSelect,
      get AddCustomer() {
        return AddCustomer;
      },
      get ListCustomer() {
        return ListCustomer;
      },
      get AddSellJh() {
        return AddSellJh;
      },
      get ListSellJh() {
        return ListSellJh;
      },
      ListCustOrder,
      ListAfter,
      AddMenus,
      RoleManager,
      UserManager,
      Category,
      ItemManager,
      BuyListManager,
      InStoreList,
      OutStoreList,
      ListStore,
      CustomerArea,
      YearMonthCount,
      EmployeeCount,
      AfterSaleCount,
      YearNumCount,
      onMounted,
      ref,
      get axios() {
        return axios;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElLoading() {
        return ElLoading;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
